import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { setAllUsers } from "../redux/actions/Actions";

import commonData from "./commonData";
// import useFetch from "./common/useFetch";

const UsersListingComponent = () => {
  const [displayData, setDisplayData] = useState(false);
  const [registered, setRegistered] = useState([]);
  const [unregistered, setUnRegistered] = useState([]);
  const [users, setUsers] = useState([]);

  const dispatch = useDispatch();

  const refreshPage = () => {
    console.log("refresh calle....");
    setUsers([]);
    setDisplayData(!displayData);
    console.log("display");
  };

  const getDataFromAPI = async (url, type) => {
    console.log("url called... ", url);
    const response = await fetch(url);
    const data = await response.json();

    if (type == "registered") {
      console.log("into reg...");
      setRegistered(data);
    } else {
      console.log("into unreg...");
      setUnRegistered(data);
    }
  };

  const updataUsersInfo = (data, type) => {
    console.log("into update user info...", commonData.registered);
    let userStatus = "";
    data.map((each) => {
      if (type == "registered") {
        userStatus = commonData.registered;
      } else {
        userStatus = commonData.unregistered;
      }
      setUsers((users) => [
        ...users,
        { id: each.id, status: userStatus, projectID: [] },
      ]);

      console.log("user after updata : ", users);
    });
  };

  useEffect(() => {
    //fetching unregistered data....
    console.log("Use Effect called..");
    getDataFromAPI(
      "https://5c3ce12c29429300143fe570.mockapi.io/api/unregisteredusers",
      "unregistered"
    ).then(() => {
      updataUsersInfo(unregistered, "unregistered");
    });

    //fetching registered data....
    getDataFromAPI(
      "https://5c3ce12c29429300143fe570.mockapi.io/api/registeredusers",
      "registered"
    ).then(() => {
      updataUsersInfo(registered, "registered");
      //        users.push({ id: each.id, status: "REG" });
    });
  }, [displayData]);

  // console.log("USER DATA AFTER FINAL UPDATE : ", users);

  //dispatching the users to the store
  dispatch(setAllUsers(users));

  return (
    <div>
      <h2> Click on the display button to get all the users information.</h2>
      <button onClick={refreshPage}> Display Users</button>
    </div>
  );
};

export default UsersListingComponent;
