import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import useFetch from "./common/useFetch";

const UsersListingComponent = () => {
  console.log("into users Listing");
  const user = { id: "", status: "" };
  const [registered, setRegistered] = useState([]);
  const [unregistered, setUnRegistered] = useState([]);
  const [users, setUsers] = useState([]);
  const dispatch = useDispatch();

  const getDataFromAPI = async (url, type) => {
    console.log("url called... ", url);
    const response = await fetch(url);
    const data = await response.json();

    if (type == "registered") {
      console.log("into reg...");
      setRegistered(data);
    } else {
      console.log("into unreg...");
      setUnRegistered(data);
    }
  };

  useEffect(() => {
    // getDataFromAPI(
    //   "https://5c3ce12c29429300143fe570.mockapi.io/api/unregisteredusers"
    // )
    //   .then(() => {
    //     getDataFromAPI(
    //       "https://5c3ce12c29429300143fe570.mockapi.io/api/registeredusers"
    //     );
    //     console.log("USER DATA AFTER UPDATE : ", registered);
    //     console.log("USER DATA AFTER UPDATE : ", unregistered);
    //   })
    //   .catch((error) => {
    //     console.log("ERROR while fetching data from API !!!", error);
    //   });

    //fetching unregistered data....
    getDataFromAPI(
      "https://5c3ce12c29429300143fe570.mockapi.io/api/unregisteredusers",
      "unregistered"
    ).then(() => {
      unregistered.map((each) => {
        //  console.log("UNREG=> ", each);
        setUsers((users) => [...users, { id: each.id, status: "UNREG" }]);
        // users.push({ id: each.id, status: "UNREG" });

        console.log("user aftert : ", users);
      });
    });

    //fetching registered data....
    getDataFromAPI(
      "https://5c3ce12c29429300143fe570.mockapi.io/api/registeredusers",
      "registered"
    ).then(() => {
      registered.map((each) => {
        setUsers((users) => [...users, { id: each.id, status: "REG" }]);
        //        users.push({ id: each.id, status: "REG" });
      });
    });
  }, []);

  // console.log("USER DATA AFTER FINAL UPDATE : ", users);

  return <div> Hi {users.map((each) => each.id)}</div>;
};

export default UsersListingComponent;
