import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import useFetch from "./common/useFetch";

const UsersListingComponent = () => {
  console.log("into users Listing");
  const [registeredUsers, setRegisteredUsers] = useState([]);
  const [unregisteredUsers, setUnRegisteredUsers] = useState([]);
  const [temp, setTemp] = useState([]);

  const eachUser = { id: "", status: "" };
  const [users, setUsers] = useState([eachUser]);
  const dispatch = useDispatch();

  const getDataFromAPI = async (url, type) => {
    const response = await fetch(url);

    console.log("response after fetch : ", response);

    // if (type == "registered") {
    //   setRegisteredUsers(await response.json());
    // } else {
    //   setUnRegisteredUsers(await response.json());
    // }

    setTemp(await response.json());
    console.log("user data : ", temp);
    temp.map((each) => {
      eachUser.id = each.id;
      eachUser.status = "UNREG";
      setUsers(...users, eachUser);
      console.log("state changed");
    });
  };

  useEffect(() => {
    getDataFromAPI(
      "https://5c3ce12c29429300143fe570.mockapi.io/api/unregisteredusers"
    )
      .then(() => {
        console.log("USER DATA AFTER UPDATE : ", users);
      })
      .catch((error) => {
        console.log("ERROR while fetching data from API !!!", error);
      });
  }, []);

  return <div>Hi</div>;
};

export default UsersListingComponent;
