import axios from "axios";
import React, { useEffect, useState } from "react";

const useFetch = (url) => {
  console.log("use Fetch CALLED>.....");
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);

  //using axios to get JSON data from the url
  const getData = async () => {
    // axios
    //   .get(url)
    //   .then((response) => {
    //     console.log("Into Then....", response);
    //     setData(response);
    //     setLoading(false);
    //     console.log("data from url : ", data);
    //   })
    //   .catch((error) => console.log("ERROR while fetching data... "));

    const response = await fetch(url);

    await response
      .then(() => {
        setData(response.json());
        setLoading(false);
        console.log("data from url : ", data);
      })
      .catch((error) => console.log("ERROR while fetching data... "));
  };

  useEffect(() => {
    getData();
  }, [url]);

  //this returns the data fetched .
  return { loading, data };
};

export default useFetch;
